package GoogleChallenges;

import java.util.HashSet;

/* 
 * A = "abcd" and B = "cdabcdab"
 * 
 * Returns :3
 * 
 * as repeat A thrice :  ab cdabcdab cd
 * 
 * 
 */


public class Repeated_String_Match {

	public static int RepeatedStringMatch(String A, String B) {
		String A_1 = A;

		HashSet h_A = new HashSet();

		for (char c:A.toCharArray()){
			h_A.add(c);
		}

		HashSet h_B = new HashSet();
		for (char c:B.toCharArray()){
			if (h_A.contains(c))
				h_B.add(c);
			else
				return -1;
		}

		if (A.length() <= 1 && A.length() > 10000 && B.length() <= 1 && B.length() > 10000 && A.length()>B.length())
			return -1;


		else {
			int l=1;
			while(!A.contains(B)) {
				l++;
				A = A.concat(A_1);

				if (A.length() > (A.length()+B.length())*2 || A.length() > 10000)
					return -1;
			}
			return l;
		}
	}



	public static void main(String[] args) {

		//		String A = "abcd";
		//		String B = "cdabcdab";

		//		String A ="abababaaba";
		//		String B = "aabaaba";
		//		String A="baaabbbaba";
		//		String B="baaabbbababaaabbbababaaabbbababaaabbbababaaabbbababaaabbbababaaabbbababaaabbbababaaabbbababaaabbbaba";
		//		String A = "a";
		//		String B = "aa";
		//		String A = "bb";
		//		String B = "bbbbbbb";
		//				String A = "abcabcabcabc";
		//				String B ="abac";

		String A = 	"a";
		String B =	"";

		System.out.println(RepeatedStringMatch(A,B));

	}

}
